void insertPosition(int value, int pos)
{
  int i = 0;
  struct Node *newNode;
  newNode = (struct Node*)malloc(sizeof(struct Node));
  newNode -> data = value;<br>
  if(head == NULL)<br>
  {<br>
    newNode -> next = NULL;<br>
    head = newNode;<br>
  }<br>
  else {<br>
    struct Node *temp = head;<br>
    for (i = 0; i < pos - 1; i++) {<br>
      temp = temp -> next;<br>
    }<br>
    newNode -> next = temp -> next;<br>
    temp -> next = newNode;<br>
  }<br>
  printf(\"\<br>Node inserted successfully\<br>\");<br>
}<br>


void pop()
{
  if (top == NULL)
    printf(\"\<br>Underflow. Stack is empty\<br>\");
  else {
    struct Node *temp = top;
    printf(\"\<br>Deleted element is: %d\", temp -> data);
    top = temp -> next;
    free(temp);
  }
}
